import React, { useState } from 'react';
import { View, Text } from 'react-native';
import bottomTab from '../style/bottomTab';
import {renderStyles as rs} from './renderStyles';
import { StyleSheet } from 'react-native';

let truc = rs;
truc.marks.chapter_label.fontSize = 16; 
truc.marks.verses_label.fontSize = 16; 
const style = StyleSheet.create(truc);


function InlineElement(props) {
    const [display, setDisplay] = useState(false);
    const toggleDisplay = () => setDisplay(!display);
    if (display) {
        return <Text>
            {props.children}
        </Text>
    } else {
        return <Text>
            {props.linkText}
        </Text>
    }
}

const renderers = {
    text: text => text,
    chapter_label: number => <Text style={{ fontSize: 'large' }}>{number}</Text>,
    verses_label: number => <Text >{number}</Text>,
    paragraph: (subType, content, footnoteNo) =>
        ["usfm:f", "usfm:x"].includes(subType) ?
            <InlineElement
                style={{}}
                linkText={subType === "usfm:f" ? `${footnoteNo}` : "*"}
            >
                {content}
            </InlineElement> :
            <Text>{content}</Text>,

    wrapper: (subType, content) => <Text >{content}</Text>,
    wWrapper: (atts, content) => content,
    mergeParas: paras => paras,
}

export { renderers };
