import React, { useState, useRef } from 'react';
import { View, TouchableOpacity, PanResponder, StyleSheet, Animated } from 'react-native';

const ResizableContainer = ({ children, canExpand = true, initialWidth }) => {
  const [width, setWidth] = useState(initialWidth);
  console.log(initialWidth)
  const pan = useRef(new Animated.ValueXY()).current;

  const panResponder = PanResponder.create({
    onStartShouldSetPanResponder: () => true,

    onPanResponderMove: Animated.event([null, { dx: pan.x }], {
      useNativeDriver: false,
      listener: (event) => {
        pan.setValue({ x: pan.x._value, y: 0 })
      }
    }),
    onPanResponderEnd: () => {
      setWidth(width + pan.x._value)

      pan.setValue({ x: 0, y: 0 })
    }
  });
  const s = !canExpand ? { flex: 1, flexGrow: 1 } : {}
  const styleText = !canExpand ? {
    height: '100%',
    overflow: 'hidden',
    borderRadius: 10,
  } : {
    height: '100%',
    overflow: 'hidden',
    width: width,
    borderRadius: 10,
  }
  const styleContainer = !canExpand ? {
    width: '100%'
  } : {
  }
  return (
    <View style={[s, { flexDirection: 'row', borderWidth: 1, borderRadius: 5 }]}>
      <View style={styleContainer}>
        <View
          style={styleText}
        >
          {children}
        </View>

      </View>

    </View>
  );
};

const styles = StyleSheet.create({
  box2: {
    backgroundColor: 'red',
    height: '100%',
    width: 20,
    position: 'absolute',
    borderRadius: 2

  },
  box: {
    backgroundColor: '#61dafb',
    height: '100%',
    width: 20,
    borderRadius: 2
  },
});

export default ResizableContainer;
