import { Text } from 'react-native-paper';
import { useCallback, useEffect, useState } from 'react';
import { View, FlatList } from 'react-native';
import { CheckboxMe } from './CheckBox';

function ChapitreSelection({ pk, bible, livre, chapitre, setChapitre }) {
  const [firstRender, setFirstRender] = useState(true);

  if (livre === 'null' || bible === 'null') {
    return <Text>{`Can't display`}</Text>;
  }

  let chapters = pk.gqlQuerySync(`{
    docSet(id: "${bible}") {
      document(bookCode: "${livre}") {
        cvIndexes {
          chapter
        }
      }
    }
  }`);

  const initialState = Array(chapters.data.docSet.document.cvIndexes.length)
    .fill(false)
    .map(() => useState(false));

  const stateValues = initialState.map(([state]) => state);

  const renderItem = useCallback(({ item }) => item, []);
  const keyExtractor = useCallback((item, index) => `chap-${index}`, []);

  useEffect(() => {
    if (!firstRender) {
      let newAC = stateValues.reduce((acc, state, index) => {
        if (state) {
          acc.push(`${index}`);
        }
        return acc;
      }, []);
      setChapitre(newAC);
    } else {
      setFirstRender(false);
    }
  }, [stateValues, firstRender, setChapitre]);

  if (chapters.data.docSet.document != null) {
    let data = [];
    chapters.data.docSet.document.cvIndexes.map((doc, id) => {
      data.push(
        <CheckboxMe
          key={id}
          num={doc.chapter}
          checked={initialState[id][0]}
          setChecked={initialState[id][1]}
        />
      );
    });
    return (
      <FlatList
        data={data}
        numColumns={3}
        keyExtractor={keyExtractor}
        renderItem={renderItem}
      />
    );
  } else {
    return <Text>{`Can't display`}</Text>;
  }
}

export { ChapitreSelection };
