import { Text, Checkbox } from 'react-native-paper';
import { useCallback, useEffect, useState } from 'react';
import { View, FlatList } from 'react-native';

function ChapitreSelection({ pk, bible, livre, chapitre, setChapitre }) {
  const [checked, setChecked] = useState(chapitre || []);

  if (livre === 'null' || bible === 'null') {
    return <Text>{`Can't display`}</Text>
  }

  let chapters = pk.gqlQuerySync(
    `{
      docSet(id: "${bible}") {
        document(bookCode: "${livre}") {
          cvIndexes {
            chapter
          }
        }
      }
    }`);

  const chaptersData = chapters.data.docSet.document?.cvIndexes || [];

  const toggleChapter = (chapter) => {
    setChecked(prevChecked =>
      prevChecked.includes(chapter)
        ? prevChecked.filter(c => c !== chapter)
        : [...prevChecked, chapter]
    );
  }

  const renderItem = useCallback(({ item }) => (
    <View key={item.chapter} style={{ flexDirection: 'column', width: 50 }}>
      <Text style={{ alignSelf: 'center' }}>{item.chapter}</Text>
      <View style={{ alignSelf: 'center' }}>
        <Checkbox
          style={{ alignSelf: 'center' }}
          value={item.chapter}
          status={checked.includes(`${item.chapter}`) ? 'checked' : 'unchecked'}
          onPress={() => toggleChapter(`${item.chapter}`)} />
      </View>
    </View>
  ), [checked]);

  const keyExtractor = useCallback((item) => `chap-${item.chapter}`, []);

  return chaptersData.length > 0 ? (
    <FlatList
      data={chaptersData}
      numColumns={5}
      keyExtractor={keyExtractor}
      renderItem={renderItem}/>
  ) : (
    <Text>{`Can't display`}</Text>
  );
}

export { ChapitreSelection }
