import { Text } from 'react-native-paper';
import * as React from 'react';
import { View } from 'react-native';
import { RadioButton } from 'react-native-paper';

function ChapitreSelection({ pk, bible, livre, chapitre ,setChapitre }) {
  const [checked, setChecked] = React.useState(chapitre);
  if(livre==='null' || bible=='null'){
    return <Text>{`Can't display`}</Text>
  }
  let chapters = pk.gqlQuerySync(
    `{
        docSet(id: "${bible}") {
          document(bookCode: "${livre}") {
            cvIndexes {
              chapter
            }
          }
        }
      }`);
  if(chapters.data.docSet.document != null){
  return <View style={{flex:10 }}>{chapters.data.docSet.document.cvIndexes.map((doc, id) =>
    <View key={id} style={{flexDirection: 'column',width:20}}>
      <Text style={{ alignSelf: 'center' }}>{doc.chapter}</Text>
      <RadioButton
        value={doc.chapter}
        status={checked === `${doc.chapter}` ? 'checked' : 'unchecked'}
        onPress={() => {
          setChecked(`${doc.chapter}`);
          setChapitre(doc.chapter)
        }} />
    </View>)}</View>
}else{
  return <Text>{`Can't display`}</Text>
}
}
export { ChapitreSelection }
