import React, { useState } from 'react';
import { View , TouchableOpacity  } from 'react-native';
import { PanGestureHandler, State } from 'react-native-gesture-handler';

const ResizableContainer = ({ children }) => {
  const [height, setHeight] = useState(100);
  const [startHeight, setStartHeight] = useState(100);

  const handleTouchStart = event => {
    const { locationX } = event.nativeEvent;
    const newValue = Math.round((locationX / 280) * 100);
    setStartHeight(newValue);
  };

  const handleTouchMove = event => {
    const { locationX } = event.nativeEvent;
    const newValue = locationX
    console.log(newValue)
    setStartHeight(newValue);
  };

  console.log('Resizable container rendered. Height:', height);

  return (
    <View style={{ height: 280 }}>
      <View
        style={{
          width: '100%',
          backgroundColor: '#f0f0f0',
          overflow: 'hidden',
          height: height,
        }}
      >
        {children}
      </View>
      <TouchableOpacity
        onTouchStart={handleTouchStart}
        onTouchMove={handleTouchMove}
      >
        <View
          style={{
            width: '100%',
            height: 40,
            backgroundColor: '#ddd',
            justifyContent: 'center',
            alignItems: 'center',
          }}
        >
          <View style={{ width: 50, height: 5, backgroundColor: '#666' }} />
        </View>
      </TouchableOpacity>
    </View>
  );
};

export default ResizableContainer;
