import React from 'react';
import { View } from 'react-native';
import { PanGestureHandler } from 'react-native-gesture-handler';
import Animated, {
  useAnimatedGestureHandler,
  useSharedValue,
  useAnimatedStyle,
} from 'react-native-reanimated';

const ResizableContainer = ({ children }) => {
  const height = useSharedValue(100);

  const panGestureHandler = useAnimatedGestureHandler({
    onStart: (_, ctx) => {
      ctx.startHeight = height.value;
    },
    onActive: (event, ctx) => {
      const newHeight = ctx.startHeight + event.translationY;
      height.value = newHeight > 0 ? newHeight : 0;
    },
  });

  const animatedStyle = useAnimatedStyle(() => {
    return {
      height: height.value,
    };
  }, []);

  return (
    <View style={{ flex: 1 }}>
      <Animated.View style={[{ width: '100%', backgroundColor: '#f0f0f0', overflow: 'hidden' }, animatedStyle]}>
        {children}
      </Animated.View>
      <PanGestureHandler onGestureEvent={panGestureHandler}>
        <View style={{ width: '100%', height: 20, backgroundColor: '#ddd' }} />
      </PanGestureHandler>
    </View>
  );
};

export default ResizableContainer;
