import { View } from 'react-native';
import { library } from '@fortawesome/fontawesome-svg-core';
import { faGear } from '@fortawesome/free-solid-svg-icons/faGear';
import { BibleSelection } from './BibleSelection';
import { LivreSelection } from './LivreSelection';
import { ChapitreSelection } from './ChapitreSelection';
import Icon from 'react-native-vector-icons/FontAwesome';
import { useState, useEffect } from 'react';
import { Modal, Text, Button } from 'react-native-paper';
import AccordionItem from './AccordionItem';
library.add(faGear);

const MyComponent = ({pk}) => {
    const [visible, setVisible] = useState(false);
    const [bible,setBible] = useState('null')
    const [livre,setLivre] =useState('null')
    const [chapitre,setChapitre] =useState([])
    const [verse,setVerse] = useState('null')
    useEffect(() => {
        setLivre('null');
        setChapitre(`null`);
        setVerse(`null`);
      }, [bible]);

      useEffect(() => {
        setChapitre([]);
        setVerse(`null`);
      }, [livre]);

      useEffect(() => {
        setVerse(`null`);
      }, [chapitre]);

    const showModal = () => setVisible(true);
    const hideModal = () => setVisible(false);
    const containerStyle = {
        alignItems: 'center',
        width: '80%',
        heigth: '20%',
        alignSelf: 'center',
        backgroundColor: 'white',
        borderRadius: 20,
        borderWidth: 1,
        borderColor: 'grey'
    };


    console.log(bible!='null'?bible:'')
    return (
        <View style={{ flex: 1 }}>
            <View style={{ height: 50 }}></View>
            <Button
                icon={() => <Icon name="gear" size={20} color="blue" />
                }

                onPress={showModal} />
            <Modal visible={visible} onDismiss={hideModal} contentContainerStyle={containerStyle}>
                <AccordionItem  title={`Bible${bible!='null'?' : '+ bible:''}`}><BibleSelection bible={bible} setBible={setBible} pk={pk}/></AccordionItem>
                <AccordionItem disable={bible!='null'?false:true} title={'Livre'}><LivreSelection pk={pk} bible={bible} livre={livre} setLivre={setLivre} /></AccordionItem>
                <AccordionItem  disable={(livre!='null')?false:true} title={"Chapitre"}><ChapitreSelection pk={pk} bible={bible} livre={livre} chapitre={chapitre} setChapitre={setChapitre} /></AccordionItem>
                <AccordionItem  disable={chapitre!=[]?false:true} title={"Verse"}><Text>chapter</Text></AccordionItem>
            </Modal>

        </View>
    );
};

export default MyComponent;
