import { Resizable } from 're-resizable';
import { useState } from 'react';
import { View, Text } from 'react-native-web';
import { ReadingScreen } from '../used/ReadingScreen';
// ...
function resizableTab() {
  const [maxHeight, setMaxHeight] = useState(50);
  function handleResize(event, { size }) {
    setMaxHeight(size.height);
  }
  const style = {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    border: "solid 1px #ddd",
    background: "#f0f0f0",
    overflow: 'auto'
  };

  return (
    <Resizable
    style={style}
    defaultSize={{
    width: '100%',
    height: 100
    }}
    maxConstraints={[100, 100]}

  >
   <ReadingScreen height={100} />   
  </Resizable>
  );
}
export { resizableTab }
