import { Text, Checkbox } from 'react-native-paper';
import { memo } from 'react';
import { View } from 'react-native';

const CheckboxMe = memo(({ num, state }) => {
  const [chec, setChec] = state;

  const handleCheckboxPress = () => {
    setChec(!chec); // Update the state directly without using the previous state
  };

  return (
    <View style={{ flexDirection: 'column', width: 50 }}>
      <Text style={{ alignSelf: 'center' }}>{num}</Text>
      <View style={{ alignSelf: 'center' }}>
        <Checkbox
          style={{ alignSelf: 'center' }}
          value={num}
          status={chec ? 'checked' : 'unchecked'}
          onPress={handleCheckboxPress} // Call the handleCheckboxPress function
        />
      </View>
    </View>
  );
});

export { CheckboxMe };
