import { Text } from 'react-native-paper';
import { useCallback, useEffect, useState } from 'react';

import { View, FlatList } from 'react-native';
import CheckboxMe from './CheckBox';

function ChapitreSelection({ pk, bible, livre, chapitre, setChapitre }) {
  if (livre === 'null' || bible == 'null') {
    return <Text>{`Can't display`}</Text>
  }

  let chapters = pk.gqlQuerySync(
    `{
        docSet(id: "${bible}") {
          document(bookCode: "${livre}") {
            cvIndexes {
              chapter
            }
          }
        }
      }`);

  const [boolChapter , setBoolChapter] = useState({});

  useEffect(() => {
    let newChapter = []
    for(let key in boolChapter){
      if(boolChapter[key])
        newChapter.push(key)
    }
    setChapitre(newChapter)
  }, [boolChapter]);

  const onChangeCheckbox = useCallback((num) => {
    setBoolChapter(prevList => ({ ...prevList, [num]: !prevList[num] }));
  }, []);

  const renderItem = useCallback(({ item, index }) => {
    return (
      <CheckboxMe
        num={item.chapter}
        isChecked={boolChapter[item.chapter] || false}
        onChange={() => onChangeCheckbox(item.chapter)}
      />
    );
  }, [boolChapter, onChangeCheckbox]);

  const keyExtractor = useCallback((item, index) => `chap-${index}`, []);

  if (chapters.data.docSet.document != null) {
    return (
      <FlatList
        data={chapters.data.docSet.document.cvIndexes}
        numColumns={3}
        keyExtractor={keyExtractor}
        renderItem={renderItem}
      />
    );
  } else {
    return <Text>{`Can't display`}</Text>
  }
}

export { ChapitreSelection }
