import React, { useState, useCallback, useEffect } from 'react';
import { View, FlatList, StatusBar } from 'react-native';
import SofriaRenderFromProskomma from './SofiraRenderFromProskomma';
import sofria2WebActions from '../../renderer/sofria2web';
import { renderers } from '../../renderer/render2reactNative';

function ReadingScreen({ livre, bible, flatListRef, pk }) {
  const [shownParas, setShownParas] = useState([]);
  const [workspace, setWorkspace] = useState({ blockId: [] });
  const [documentResult, setDocumentResult] = useState(null);
  const [output, setOutput] = useState({});
  const [context] = useState({});
  const config = {
    showWordAtts: false,
    showTitles: true,
    showHeadings: true,
    showIntroductions: true,
    showFootnotes: true,
    showXrefs: true,
    showParaStyles: true,
    showCharacterMarkup: true,
    showChapterLabels: true,
    showVersesLabels: true,
    nbBlock: 1,
    chapters: [],
    selectedBcvNotes: [],
    displayPartOfText: { state: 'begin' },
    bcvNotesCallback: (bcv) => {
      // Do something with bcv
    },
    renderers,
  };

  const renderer = new SofriaRenderFromProskomma({
    proskomma: pk,
    actions: sofria2WebActions,
  });

  const loadMoreItems = useCallback(() => {
    if (!documentResult) return;

    setShownParas((prevShownParas) => {
      if (workspace.blockId.length < config.nbBlock) {
        workspace.chapters = [
          `${documentResult.data.document.cIndexes.shift().chapter}`,
        ];
      }

      renderer.renderDocument1({
        docId: documentResult.data.document.id,
        config,
        context,
        workspace,
        output,
      });

      return [
        ...prevShownParas,
        ...output.paras.slice(
          prevShownParas.length,
          prevShownParas.length + config.nbBlock
        ),
      ];
    });
  }, [
    documentResult,
    config,
    context,
    workspace,
    output,
    renderer,
    setShownParas,
    pk,
  ]);

  useEffect(() => {
    const documentQuery = `
      {
        document(docSetId: "${bible}" withBook: "${livre}"){
          id
          cIndexes {
            chapter
          }
        }
      }
    `;

    const documentResult = pk.gqlQuerySync(documentQuery);
    setDocumentResult(documentResult);
  }, [pk, bible, livre]);

  useEffect(() => {
    loadMoreItems();
  }, [loadMoreItems]);

  const renderItem = useCallback(({ item }) => item, []);

  const keyExtractor = useCallback(
    (item, index) => `para-${index}-${livre}-${bible}`,
    [livre, bible]
  );

  return (
    <View style={{ flex: 1 }}>
      <FlatList
        style={{ height: '100%', width: '100%' }}
        removeClippedSubviews={true}
        data={shownParas}
        renderItem={renderItem}
        keyExtractor={keyExtractor}
        // ref={flatListRef}
        onEndReached={loadMoreItems}
        scrollEventThrottle={16} // Add scrollEventThrottle for better performance
        onEndReachedThreshold={0.8} // Trigger loadMoreItems when the user reaches 50% from the end
      />
      <StatusBar style="auto" />
    </View>
  );
}

export { ReadingScreen };
