import { queryOneChapterOfOneDocSets } from '../graphql/Query/query';
const { Proskomma } = require('proskomma-core');
import { View, ScrollView, Text, StatusBar } from 'react-native';
import bottomTab from '../style/bottomTab';

const succinctlsg = require('../succinct/lsg.json');
const pk = new Proskomma([
  {
    name: "source",
    type: "string",
    regex: "^[^\\s]+$",
  },
  {
    name: "project",
    type: "string",
    regex: "^[^\\s]+$",
  },
  {
    name: "revision",
    type: "string",
    regex: "^[^\\s]+$",
  },
]);

function multipleReplace(query,tabl){
  let ret = query;
  for(const [s,r] of tabl){
    ret = ret.replace(s,r);
  }
  return ret
}

pk.loadSuccinctDocSet(queryOneChapterOfOneDocSets);
let book = pk.gqlQuerySync(multipleReplace([["%idChapter%", "1"],["%docSetId%","local_lsg_1"],["%bookCode%","TIT"]]));





function MainPage() {
  return (
    <ScrollView style={bottomTab.mainContent}>
      <View>
        {book.data.docSet.documents.map(
          (doc, idd) => (<View key={`chapter ${idd}`}><Text>Chapitre {idd + 1}</Text>
            {doc.cvIndex.verses.map(
              (vers, idvs) => (
                <View key={`verses ${idd}-${idvs}`}> 
                  <Text> {idvs}</Text>
                  <Text>{vers.verse[0]?.text}</Text>
                </View>))}
          </View>))}
        <StatusBar style="auto" />
      </View>
    </ScrollView>
  );

}
export { MainPage }
