import { Text } from 'react-native-paper';
import * as React from 'react';
import { View } from 'react-native';
import { RadioButton } from 'react-native-paper';

function BibleSelection({ pk, Bible, setBible }) {
  const [checked, setChecked] = React.useState('first');
  let docSetids = pk.gqlQuerySync(
    `
    {
      docSets 
      {
        id
      }
    }`);
  return <View>{docSetids.data.docSets.map((doc,id) => 
  <RadioButton
  key={id}
    value={doc.id}
    status={ checked === `${doc.id}` ? 'checked' : 'unchecked' }
    onPress={() => setChecked(`${doc.id}`)}
  />)}</View>
}
export { BibleSelection }
