import React, { useRef, useState } from 'react';
import { View, StyleSheet } from 'react-native';

const BoxComponent = () => {
  const [box1Width, setBox1Width] = useState(100);
  const box2Ref = useRef(null);

  const handleBox1Layout = (event) => {
    const { width } = event.nativeEvent.layout;
    setBox1Width(width);
  };

  const handleBox2Layout = () => {
    const width = box2Ref.current?.getBoundingClientRect().width || 0;
    setBox1Width(width);
  };

  return (
    <View style={styles.container}>
      <View style={[styles.box, { width: box1Width }]} onLayout={handleBox1Layout} />
      <View style={styles.box} onLayout={handleBox2Layout} ref={box2Ref} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  box: {
    height: 50,
    borderWidth: 1,
    borderColor: 'black',
  },
});

export default BoxComponent;
