import { Resizable, onResize,ResizableBox } from 'react-resizable';
import { useState } from 'react';
import { View, Text } from 'react-native-web';
// ...
function resizableTab() {
  const [state,setState] = useState({
      width: 100,
      height: 50,
    })

  // On top layout
  onResize = (event, { node, size, handle }) => {
    setState({ width: size.width, height: size.height });
  };
  return (
    <ResizableBox height={state.height} width={state.width} onResize={onResize}>
      <View className="box" style={{ width: state.width + 'px', height: state.height + 'px',borderWidth: 5, }}>
        <Text>Change my size!</Text>
      </View>
    </ResizableBox>
  );
}
export { resizableTab }
