import { Text, Checkbox } from 'react-native-paper';
import { useCallback, useEffect, useState, useReducer } from 'react';
import { View, FlatList, StyleSheet } from 'react-native';

export const checkboxesReducer = (state, action) => {
  switch (action.type) {
    case 'toggle':
      return { ...state, [action.id+1]: !state[action.id] };
    default:
      throw new Error();
  }
};

function ChapitreSelection({ pk, bible, livre }) {
  const [checkboxes, dispatch] = useReducer(checkboxesReducer, [].reduce((acc, id) => ({ ...acc, [id]: true }), {}));
  const toggleCheckbox = useCallback((id) => dispatch({ type: 'toggle', id }), [dispatch]);

  if (livre === 'null' || bible == 'null') {
    return <Text>{`Can't display`}</Text>
  }

  let chapters = pk.gqlQuerySync(
    `{
        docSet(id: "${bible}") {
          document(bookCode: "${livre}") {
            cvIndexes {
              chapter
            }
          }
        }
      }`
  );

  const renderItem = useCallback(
    ({ item }) => {
      const { id, num } = item;
      return (
        <View style={styles.checkboxContainer}>
          <Text style={styles.centeredText}>{num}</Text>
          <View style={styles.centeredCheckbox}>
            <Checkbox
              status={checkboxes[id] ? 'checked' : 'unchecked'}
              onPress={() => toggleCheckbox(id)}
            />
          </View>
        </View>
      );
    },
    [toggleCheckbox, checkboxes]
  );

  const keyExtractor = useCallback((item) => `chap-${item.id}`, []);

  const data = chapters.data.docSet.document.cvIndexes.map((doc, id) => ({
    id,
    num: doc.chapter,
  }));

  if (data.length > 0) {
    return (
      <FlatList
        data={data}
        numColumns={3}
        keyExtractor={keyExtractor}
        renderItem={renderItem}
      />
    );
  } else {
    return <Text>{`Can't display`}</Text>
  }
}

const styles = StyleSheet.create({
  checkboxContainer: { flexDirection: 'column', width: 50 },
  centeredText: { alignSelf: 'center' },
  centeredCheckbox: { alignSelf: 'center' },
});

export { ChapitreSelection }
