import React, { useState, useRef } from 'react';
import { View, TouchableOpacity, PanResponder, StyleSheet, Animated } from 'react-native';

const ResizableContainer = ({ children, canExpand = true, initialWidth }) => {
  const [width, setWidth] = useState(initialWidth);
  console.log(initialWidth)
  const pan = useRef(new Animated.ValueXY()).current;

  const panResponder = PanResponder.create({
    onStartShouldSetPanResponder: () => true,

    onPanResponderMove: Animated.event([null, { dx: pan.x }], {
      useNativeDriver: false,
      listener: (event) => {
        pan.setValue({ x: pan.x._value, y: 0 })
      }
    }),
    onPanResponderEnd: () => {
      setWidth(width + pan.x._value)

      pan.setValue({ x: 0, y: 0 })
    }
  });
  const s = !canExpand ? { flex: 1, flexGrow: 1 } : {}
  const styleText = !canExpand ? {
    height: '100%',
    borderRadius: 10,
  } : {
    height: '100%',
    width: width,
    borderRadius: 10,
  }
  const styleContainer = !canExpand ? {
    width: '100%',
    flex: 1
  } : {
    flex: 1
  }
  return (

    <View style={[s, { flexDirection: 'row', borderRadius: 5 }]}>
      <View style={styleContainer}>
        <View
          style={styleText}
        >
          <View style={[{ position: 'absolute' }, { zIndex: 3 }]}>
            <Button key={Math.random()} style={{ position: 'absolute' }}
              icon={() => <Icon name="gear" size={20} color="blue" />
              }
              onPress={() => setIsActive(true)} />
          </View>
          <ConfigDrawer key={Math.random()} widthParent={Dimensions.get('window').width} pk={pk} setIsActive={setIsActive} isActive={isActive} setBibleParent={setBible} bibleParent={bible} livreParent={livre} setLivreParent={setLivre} />
          <ReadingScreen
            key={`reading-screen-${index}`}
            flatListRef={flatListRefs[index]}
            pk={pk}
            livre={livre}
            bible={bible}
          />
        </View>

      </View>
      {canExpand ? (
        <View>
          <View
            style={[
              styles.box2,
            ]}
          />
          <Animated.View
            {...panResponder.panHandlers}
            style={[pan.getLayout(), styles.box]}
          />
        </View>
      ) : <View />}
    </View>
  );
};

const styles = StyleSheet.create({
  box2: {
    backgroundColor: '#61dafb',
    opacity: 0.6,
    height: '100%',
    width: 20,
    position: 'absolute',
    borderRadius: 5,
    borderWidth: 1
  },
  box: {
    backgroundColor: '#61dafb',
    height: '100%',
    width: 20,
    borderRadius: 5,
    borderWidth: 1,
    zIndex: 3,
  },
});

export default ResizableContainer;
