import { queryOneDocument } from '../../graphql/Query/query';
import { View, ScrollView, Text, StatusBar, FlatList } from 'react-native';
import SofriaRenderFromProskomma from './SofiraRenderFromProskommaNew';
import sofria2WebActions from '../../renderer/sofria2web';
import { renderers } from '../../renderer/render2reactNative';
import React, { useState, useCallback, useEffect } from 'react';
import ConfigDrawer from './TextConfig/configDrawer';

function multipleReplace(query, tabl) {
  let ret = query;
  for (const [s, r] of tabl) {
    ret = ret.replace(s, r);
  }
  return ret;
}

function ReadingScreen({ livre, bible, flatListRef, pk }) {
  const output = {};
  const context = {};
  const workspace = {};
  const [shownParas, setShownParas] = useState([]);
  const [documentResult, setDocumentResult] = useState(null);
  const [renderer, setRenderer] = useState(null);
  const [config, setConfig] = useState(null);
  const [state, setState] = useState('begin');

  useEffect(() => {
    let documentQuery = `
      {
        document(docSetId: "${bible}" withBook: "${livre}"){
          id
          cIndexes {
            chapter
          }
        }
      }
    `;

    pk.gqlQuery(documentQuery)
      .then((result) => {
        setDocumentResult(result);
        console.log(result);
      })
      .catch((error) => {
        console.log("Error fetching document:", error);
      });
  }, [livre, bible]);

  useEffect(() => {
    if (documentResult) {
      console.log(documentResult);

      const newRenderer = new SofriaRenderFromProskomma({
        proskomma: pk,
        actions: sofria2WebActions,
      });

      setRenderer(newRenderer);

      const newState = 'begin';
      const newConfig = {
        showWordAtts: false,
        showTitles: true,
        showHeadings: true,
        showIntroductions: true,
        showFootnotes: true,
        showXrefs: true,
        showParaStyles: true,
        showCharacterMarkup: true,
        showChapterLabels: true,
        showVersesLabels: true,
        nbBlock: 1,
        chapters: [`${documentResult.data.document.cIndexes.shift().chapter}`],
        selectedBcvNotes: [],
        displayPartOfText: { newState },
        bcvNotesCallback: (bcv) => {
          setBcvNoteRef(bcv);
        },
        renderers,
      };

      setState(newState);
      setConfig(newConfig);

      try {
        newRenderer.renderDocument1({
          docId: documentResult.data.document.id,
          config: newConfig,
          context,
          workspace,
          output,
        });
        setShownParas(output.paras);
      } catch (err) {
        console.log("Renderer", err);
        throw err;
      }

      newConfig.displayPartOfText.state = 'continue';
    }
  }, [documentResult]);

  const loadMoreItems = () => {
    console.log(documentResult);
    setShownParas((prevState) => {
      console.log('rien mais ici');
      console.log(bible);
      console.log(livre);
      if (documentResult?.data.document.cIndexes.length !== 0) {
        if (workspace.blockId.length < config.nbBlock) {
          workspace.chapters = [
            `${documentResult.data.document.cIndexes.shift().chapter}`,
          ];
          renderer.renderDocument1({
            docId: documentResult.data.document.id,
            config,
            context,
            workspace,
            output,
          });
          console.log(documentResult.data.document.id);
          return [
            ...shownParas,
            ...output.paras.slice(
              shownParas.length,
              shownParas.length + config.nbBlock
            ),
          ];
        } else {
          renderer.renderDocument1({
            docId: documentResult.data.document.id,
            config,
            context,
            workspace,
            output,
          });

          return [
            ...shownParas,
            ...output.paras.slice(
              shownParas.length,
              shownParas.length + config.nbBlock
            ),
          ];
        }
      } else {
        if (workspace.blockId.length < config.nbBlock) {
          renderer.renderDocument1({
            docId: documentResult.data.document.id,
            config,
            context,
            workspace,
            output,
          });

          return [
            ...shownParas,
            ...output.paras.slice(
              shownParas.length,
              shownParas.length + config.nbBlock
            ),
          ];
        } else {
          renderer.renderDocument1({
            docId: documentResult.data.document.id,
            config,
            context,
            workspace,
            output,
          });

          if (workspace.blockId.length === 0) {
            return [sh];
          }

          if (workspace.blockId.length < config.nbBlock) {
            workspace.chapters = [
              `${documentResult.data.document.cIndexes.shift().chapter}`,
            ];
            renderer.renderDocument1({
              docId: documentResult.data.document.id,
              config,
              context,
              workspace,
              output,
            });

            return [
              ...shownParas,
              ...output.paras.slice(
                shownParas.length,
                shownParas.length + config.nbBlock
              ),
            ];
          } else {
            renderer.renderDocument1({
              docId: documentResult.data.document.id,
              config,
              context,
              workspace,
              output,
            });

            return [
              ...shownParas,
              ...output.paras.slice(
                shownParas.length,
                shownParas.length + config.nbBlock
              ),
            ];
          }
        }
      }
    });
  };

  const renderItem = useCallback(({ item }) => item, []);

  const keyExtractor = useCallback(
    (item, index) => `para-${index}-${livre}-${bible}`,
    [livre, bible]
  );

  return (
    <View style={{ flex: 1 }}>
      <FlatList
        style={{ height: '100%', width: '100%' }}
        removeClippedSubviews={true}
        data={shownParas}
        renderItem={renderItem}
        keyExtractor={keyExtractor}
        //ref={flatListRef}
        onEndReached={loadMoreItems}
        scrollEventThrottle={16} // Add scrollEventThrottle for better performance
        onEndReachedThreshold={0.8} // Trigger loadMoreItems when the user reaches 50% from the end
      />
      <StatusBar style="auto" />
    </View>
  );
}

export { ReadingScreen };
