import React from 'react';
import { View } from 'react-native';
import { PanGestureHandler, State } from 'react-native-gesture-handler';
import Animated, { Value, event, cond, add, eq } from 'react-native-reanimated';

const ResizableContainer = ({ children }) => {
  const translateY = new Value(0);
  const gestureState = new Value(State.UNDETERMINED);

  const onGestureEvent = event([
    {
      nativeEvent: {
        translationY: translateY,
        state: gestureState,
      },
    },
  ]);

  const height = cond(
    eq(gestureState, State.ACTIVE),
    add(100, translateY),
    100
  );

  const onHandlerStateChange = event([
    {
      nativeEvent: (event) => {
        console.log('Gesture state changed:', event.state);
      },
    },
  ]);

  console.log('Resizable container rendered.');

  return (
    <View style={{ flex: 1 }}>
      <Animated.View
        style={{
          width: '100%',
          backgroundColor: '#f0f0f0',
          overflow: 'hidden',
          height: height,
        }}
      >
        {children}
      </Animated.View>
      <PanGestureHandler
        onGestureEvent={onGestureEvent}
        onHandlerStateChange={onHandlerStateChange}
      >
        <View
          style={{
            width: '100%',
            height: 40,
            backgroundColor: '#ddd',
            justifyContent: 'center',
            alignItems: 'center',
          }}
        >
          <View style={{ width: 50, height: 5, backgroundColor: '#666' }} />
        </View>
      </PanGestureHandler>
    </View>
  );
};

export default ResizableContainer;
