import { View } from 'react-native';
import { library } from '@fortawesome/fontawesome-svg-core';
import { faGear } from '@fortawesome/free-solid-svg-icons/faGear';
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome';
import * as React from 'react';
import { Modal, Portal, Text, Button, Provider } from 'react-native-paper';
import AccordionItem from './AccordionItem';
library.add(faGear);
const MyComponent = () => {
    const [visible, setVisible] = React.useState(false);

    const showModal = () => setVisible(true);
    const hideModal = () => setVisible(false);
    const containerStyle = {alignItems:'center' ,width:'80%', heigth:'20%',alignSelf:'center', background:'blue'};



    return (
        <View>
        <View style={{height:400}}></View>
        <Provider>
            <Portal>

                <Modal visible={visible} onDismiss={hideModal} contentContainerStyle={containerStyle}>
                <AccordionItem disable={true} title={"Bible"}><Text>chapter</Text></AccordionItem>
                <AccordionItem title={"Livre"}><Text>chapter</Text></AccordionItem>
                <AccordionItem title={"chapter"}><Text>chapter</Text></AccordionItem>
                <AccordionItem title={"verse"}><Text>chapter</Text></AccordionItem>
                </Modal>
            </Portal>
            <Button
                icon={() => <FontAwesomeIcon icon={faGear} size={20} color="blue" />}
                onPress={showModal} />
        </Provider >
        </View>
    );
};

export default MyComponent;
