import React, { useState, useCallback, useEffect } from 'react';
import { View, StatusBar, FlatList } from 'react-native';
import SofriaRenderFromProskomma from './SofiraRenderFromProskommaNew';
import sofria2WebActions from '../../renderer/sofria2web';
import { renderers } from '../../renderer/render2reactNative';

function ReadingScreen({ livre, bible, flatListRef, pk }) {
  const [data, setData] = useState([]);
  const [workspace, setWorkspace] = useState({});
  const [config, setConfig] = useState({});
  const [output, setOutput] = useState({});
  const [context, setContext] = useState({});
  const [documentResult, setDocumentResult] = useState(null);

  const queryStartDocument = () => {
    let documentQuery = `
      {
        document(docSetId: "${bible}" withBook: "${livre}") {
          id
          cIndexes {
            chapter
          }
        }
      }
    `;

    const result = pk.gqlQuerySync(documentQuery);
    setDocumentResult(result);

    const renderer = new SofriaRenderFromProskomma({
      proskomma: pk,
      actions: sofria2WebActions,
    });

    const state = 'begin';
    const initialConfig = {
      showWordAtts: false,
      showTitles: true,
      showHeadings: true,
      showIntroductions: true,
      showFootnotes: true,
      showXrefs: true,
      showParaStyles: true,
      showCharacterMarkup: true,
      showChapterLabels: true,
      showVersesLabels: true,
      nbBlock: 1,
      chapters: [`${result.data.document.cIndexes.shift().chapter}`],
      selectedBcvNotes: [],
      displayPartOfText: { state },
      bcvNotesCallback: (bcv) => {
        setBcvNoteRef(bcv);
      },
      renderers,
    };

    setConfig(initialConfig);
    setWorkspace({});
    setOutput({});
    setContext({});

    try {
      const newOutput = {};
      const newContext = {};
      const newWorkspace = {};
      renderer.renderDocument1({
        docId: result.data.document.id,
        config: initialConfig,
        context: newContext,
        workspace: newWorkspace,
        output: newOutput,
      });
      initialConfig.displayPartOfText.state = 'continue';
      setData(newOutput.paras);
      setOutput(newOutput);
      setContext(newContext);
      setWorkspace(newWorkspace);
    } catch (err) {
      console.log('Renderer', err);
      throw err;
    }
  };

  const loadMoreItems = () => {
    if (!documentResult) {
      return;
    }

    const newWorkspace = { ...workspace };
    const newOutput = { ...output };
    const newContext = { ...context };
    const newConfig = { ...config }
    newWorkspace.chapters = [`${result.data.document.cIndexes.shift().chapter}`]

    try {
      renderer.renderDocument1({
        docId: result.data.document.id,
        config: initialConfig,
        context: newContext,
        workspace: newWorkspace,
        output: newOutput,
      });
      initialConfig.displayPartOfText.state = 'continue';
      setData(newOutput.paras);
      setOutput(newOutput);
      setContext(newContext);
      setWorkspace(newWorkspace);
    } catch (err) {
      console.log('Renderer', err);
      throw err;
    }



    setData(newOutput.paras);
    setOutput(newOutput);
    setContext(newContext);
    setWorkspace(newWorkspace);
  };

  useEffect(() => {
    queryStartDocument();
  }, [bible, livre, pk]);

  const renderItem = useCallback(({ item }) => item, []);

  const keyExtractor = useCallback(
    (item, index) => `para-${index}-${livre}-${bible}`,
    [livre, bible]
  );

  return (
    <View style={{ flex: 1 }}>
      <FlatList
        style={{ height: '100%', width: '100%' }}
        removeClippedSubviews={true}
        data={data}
        renderItem={renderItem}
        keyExtractor={keyExtractor}
        // ref={flatListRef}
        onEndReached={loadMoreItems}
        scrollEventThrottle={16} // Add scrollEventThrottle for better performance
        onEndReachedThreshold={0.8} // Trigger loadMoreItems when the user reaches 50% from the end
      />
      <StatusBar style="auto" />
    </View>
  );
}

export { ReadingScreen };
