import SofriaRenderFromProskomma from './SofiraRenderFromProskommaNew';
import sofria2WebActions from '../../renderer/sofria2web';
const fse = require('fs-extra');
const fs = require('fs');

function Tabletest({ pk }) {

    const usfm = fse.readFileSync(path.resolve(path.join('table.usfm'))).toString();
    pk.importDocument({ 'lang': 'eng', 'abbr': 'francl' }, 'usfm', usfm);

    let documentQuery = `
    {
      document(docSetId: "eng_francl" withBook: "1CH"){
        id
        cIndexes {
          chapter
        }
    }}`

    const documentResult = pk.gqlQuerySync(documentQuery)
    console.log(documentResult)
    const renderer = new SofriaRenderFromProskomma({
        proskomma: pk,
        actions: sofria2WebActions,
    });

    const output = {};
    const context = {};
    const workspace = {};
    try {

        renderer.renderDocument1({
            docId: documentResult.data.document.id,
            config,
            context,
            workspace,
            output,
        });

    } catch (err) {
        console.log("Renderer", err);
        throw err;
    }

    return <View>{output.paras}</View>
}  
export {Tabletest}
