import React, { useState } from 'react';
import { View } from 'react-native';
import { PanGestureHandler, State } from 'react-native-gesture-handler';

const ResizableContainer = ({ children }) => {
  const [height, setHeight] = useState(100);
  const [startHeight, setStartHeight] = useState(100);
  const [gestureState, setGestureState] = useState(State.UNDETERMINED);

  const onGestureEvent = (event) => {
    if (event.nativeEvent.state === State.BEGAN) {
      setStartHeight(height);
    }
    if (event.nativeEvent.state === State.ACTIVE) {
      const newHeight = startHeight + event.nativeEvent.translationY;
      setHeight(newHeight > 0 ? newHeight : 0);
    }
    setGestureState(event.nativeEvent.state);
  };

  console.log('Resizable container rendered.');

  return (
    <View style={{ flex: 1 }}>
      <View
        style={{
          width: '100%',
          backgroundColor: '#f0f0f0',
          overflow: 'hidden',
          height: height,
        }}
      >
        {children}
      </View>
      <PanGestureHandler
        onGestureEvent={onGestureEvent}
        onHandlerStateChange={onGestureEvent}
      >
        <View
          style={{
            width: '100%',
            height: 40,
            backgroundColor: '#ddd',
            justifyContent: 'center',
            alignItems: 'center',
          }}
        >
          <View style={{ width: 50, height: 5, backgroundColor: '#666' }} />
        </View>
      </PanGestureHandler>
    </View>
  );
};

export default ResizableContainer;
