import { Text } from 'react-native-paper';
import { useCallback, useEffect, useState, useRef,useReducer } from 'react';

import { View, FlatList } from 'react-native';
import { Checkbox } from 'react-native-paper';

const checkboxesReducer = (state, action) => {
  switch (action.type) {
    case 'toggle':
      return { ...state, [action.id]: !state[action.id] };
    default:
      throw new Error();
  }
};

function ChapitreSelection({ pk, bible, livre, chapitre, setChapitre }) {
  console.log('renderchapitreSelection')
  let firstRender = useRef(true);
  const [checkboxes, dispatch] = useReducer(checkboxesReducer, {});

  if (livre === 'null' || bible == 'null') {
    return <Text>{`Can't display`}</Text>
  }
  let chapters = pk.gqlQuerySync(
    `{
        docSet(id: "${bible}") {
          document(bookCode: "${livre}") {
            cvIndexes {
              chapter
            }
          }
        }
      }`);




  const toggleCheckbox = (id) => dispatch({ type: 'toggle', id });
  const renderItem = useCallback(({ item }) => item, []);
  const keyExtractor = useCallback((item, index) => `chap-${index}`, []);




  if (chapters.data.docSet.document != null) {
    let data = []
    chapters.data.docSet.document.cvIndexes.map((doc, id) => {
      data.push(
        <View style={{ flexDirection: 'column', width: 50 }}>
          <Text style={{ alignSelf: 'center' }}>{id+1}</Text>
          <View style={{ alignSelf: 'center' }}>
            <Checkbox
              key={id}
              status={checkboxes[id] ? 'checked' : 'unchecked'}
              onPress={() => toggleCheckbox(id)}
            />
          </View>
        </View>)
    })
    return <FlatList
      data={data}
      numColumns={3}
      keyExtractor={keyExtractor}
      renderItem={renderItem} />
  } else {
    return <Text>{`Can't display`}</Text>
  }
}
export { ChapitreSelection }
