import { Resizable, onResize } from 'react-resizable';

// ...
function resizableTab() {
let state = {
    width: 200,
    height: 200,
  };

  // On top layout
  onResize = (event, {node, size, handle}) => {
    this.setState({width: size.width, height: size.height});
  };
    return (
      <Resizable height={state.height} width={state.width} onResize={onResize}>
        <div className="box" style={{width: state.width + 'px', height: state.height + 'px'}}>
          <span>Contents</span>
        </div>
      </Resizable>
    );
  }
export {resizableTab}
