import React, { useState } from 'react';
import { View, Text } from 'react-native';
import bottomTab from '../style/bottomTab';
import { renderStyles as rs } from './renderStyles';
import { StyleSheet } from 'react-native';

const styles = StyleSheet.create(rs);

const getStyles = (type, subType) => {
    if (!styles[type]) {
        throw new Error(`Unknown style type '${type}'`);
    }
    if (!styles[type][subType]) {
        console.log(`No styles for ${type}/${subType}`)
        return styles[type].default;
    }
    return { ...styles[type].default, ...styles[type][subType] };
}

function InlineElement(props) {
    const [display, setDisplay] = useState(false);
    const toggleDisplay = () => setDisplay(!display);
    if (display) {
        return <Text
            style={{
                ...props.style,
                paddingLeft: "0.5",
                paddingRight: "0.5",
                backgroundColor: "#CCC",
                marginTop: "1",
                marginBottom: "1"
            }}
            onClick={toggleDisplay}
        >
            {props.children}
        </Text>
    } else {
        return <Text
            style={{
                verticalAlign: "super",
                fontSize: "x-small",
                fontWeight: "bold",
                marginRight: "0.25",
                padding: "2",
                backgroundColor: "#CCC"
            }}
            onClick={toggleDisplay}
        >
            {props.linkText}
        </Text>
    }
}


const renderers = {
    text: text => text,
    chapter_label: number => <Text style={getStyles('marks', "chapter_label")}>{number}</Text>,
    verses_label: (number, bcv, bcvCallback) =>
        bcv && bcv.length === 3 ?
            <Text
                onClick={() => bcvCallback(bcv)} style={{
                    ...getStyles('marks', "verses_label"),
                    textDecoration: "underline",
                    color: "#00D"
                }}
            >
                {number}
            </Text> :
            <Text style={getStyles('marks', "verses_label")}>{number}</Text>,


    paragraph: (subType, content, footnoteNo) =>
        ["usfm:f", "usfm:x"].includes(subType) ?
            <InlineElement
                style={getStyles('paras', subType)}
                linkText={subType === "usfm:f" ? `${footnoteNo}` : "*"}
            >
                {content}
            </InlineElement> :
            <Text style={getStyles('paras', subType)}>{content}</Text>,

    wrapper: (subType, content) => <Text style={getStyles('wrappers', subType)}>{content}</Text>,
    wWrapper: (atts, content) => Object.keys(atts).length === 0 ?
        content :
        <Text
            style={{
                display: "inline-block",
                verticalAlign: "top",
                textAlign: "center"
            }}
        >
            <Text>{content}</Text>
            {
                Object.entries(atts).map(
                    a =>
                        <Text
                            style={{
                                fontSize: "xx-small",
                                fontWeight: "bold"
                            }}
                        >
                            {`${a[0]} = ${a[1]}`}
                        </Text>
                )
            }
        </Text>,
    mergeParas: paras => paras,
}

export { renderers };
