import { Resizable, onResize } from 'react-resizable';
import { useState } from 'react';
// ...
function resizableTab() {
  const [state,setState] = useState({
      width: 50,
      height: 50,
    })

  // On top layout
  onResize = (event, { node, size, handle }) => {
    setState({ width: size.width, height: size.height });
  };
  return (
    <Resizable height={state.height} width={state.width} onResize={onResize}>
      <View className="box" style={{ width: state.width + 'px', height: state.height + 'px' }}>
        <Text>Change my size!</Text>
      </View>
    </Resizable>
  );
}
export { resizableTab }
