import React, { useRef, useState } from 'react';
import { StyleSheet, View, PanResponder, Animated } from 'react-native';

const BoxComponent = () => {
  const [boxWidth, setBoxWidth] = useState(0);
  const pan = useRef(new Animated.Value(0)).current;

  const panResponder = PanResponder.create({
    onStartShouldSetPanResponder: () => true,
    onPanResponderMove: Animated.event([null, { dx: pan }], { useNativeDriver: false }),
    onPanResponderRelease: () => {
      // Implement any logic you need when the pan is released
    },
  });

  const handleLayout = (event) => {
    const { width } = event.nativeEvent.layout;
    setBoxWidth(width);
  };

  const leftBoxStyle = {
    flex: pan.interpolate({
      inputRange: [0, boxWidth],
      outputRange: [1, 0],
      extrapolate: 'clamp',
    }),
  };

  const rightBoxStyle = {
    flex: pan.interpolate({
      inputRange: [-boxWidth, 0],
      outputRange: [0, 1],
      extrapolate: 'clamp',
    }),
  };

  return (
    <View style={styles.container}>
      <View style={styles.box} />
      <View style={styles.box} onLayout={handleLayout}>
        <Animated.View {...panResponder.panHandlers} style={[styles.middleBox, { width: boxWidth }]} />
      </View>
      <View style={[styles.box, leftBoxStyle]} />
      <View style={[styles.box, rightBoxStyle]} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'row',
  },
  box: {
    flex: 1,
    borderWidth: 1,
    borderRadius: 2,
  },
  middleBox: {
    backgroundColor: '#61dafb',
    zIndex: 3,
    width: 15,
  },
});

export default BoxComponent;
