import React, { useRef, useState } from 'react';
import { ReadingScreen } from '../used/ReadingScreen';
import ResizableContainer from './ResizableContainer';
import { View } from 'react-native';
import { Dimensions } from 'react-native';
import ConfigDrawer from './TextConfig/configDrawer'
import { Button } from 'react-native-paper';
import Icon from 'react-native-vector-icons/FontAwesome';
import { StyleSheet } from 'react-native';


function createRefsArray(length) {
  return Array.from({ length }, () => useRef(null));
}
function ResizableTab({ pk }) {

  const panResponder = PanResponder.create({
    onStartShouldSetPanResponder: () => true,

    onPanResponderMove: Animated.event([null, { dx: pan.x }], {
      useNativeDriver: false,
      listener: (event) => {
        pan.setValue({ x: pan.x._value, y: 0 })
      }
    }),
    onPanResponderEnd: () => {
      setWidth(width + pan.x._value)

      pan.setValue({ x: 0, y: 0 })
    }
  });

  console.log("resiTab")
  const numberText = 2;
  const arrayHooks = [];
  const flatListRefs = createRefsArray(numberText);
  const [isUserScroll, setIsUserScroll] = useState(true);
  const [scrollLock, setScrollLock] = useState(false);

  const onScroll = (event, currentIndex) => {
    if (scrollLock) return;

    setScrollLock(true);
    const offsetX = event.nativeEvent.contentOffset.x;


    if (isUserScroll) {
      setIsUserScroll(false);
      flatListRefs.forEach((ref, index) => {
        if (index !== currentIndex) {
          ref.current.scrollToOffset({ offset: offsetX, animated: false });
        }
      });
    } else {
      setIsUserScroll(true);
    }

    setTimeout(() => setScrollLock(false), 20); // Release the lock after 100ms
  };




  const resizableContainers = Array.from({ length: numberText }, (_, index) => {

    const [isActive, setIsActive] = useState(false)
    const [bible, setBible] = useState('local_test_1')
    const [livre, setLivre] = useState('GEN')
    console.log(Dimensions.get('window').width * (1 / numberText))
    arrayHooks.push([isActive, setIsActive, bible, setBible, livre, setLivre])
    return (
      <
        View
      >
        <View style={[{ position: 'absolute' }, { zIndex: 3 }]}>
          <Button key={Math.random()} style={{ position: 'absolute' }}
            icon={() => <Icon name="gear" size={20} color="blue" />
            }
            onPress={() => setIsActive(true)} />
        </View>

        <ReadingScreen
          key={`reading-screen-${index}`}
          flatListRef={flatListRefs[index]}
          pk={pk}
          livre={livre}
          bible={bible}
        />
        {index != numberText - 1 ?

          <Animated.View
            {...panResponder.panHandlers}
            style={[pan.getLayout(), styles.box]}
          /> : <></>}
      </View >

    )
  });

  return <View style={{ flex: 1 }}>
    <View style={{
      flexDirection: 'row', flex: 1, alignContent: 'space-between',
    }}>

      {resizableContainers}
      {arrayHooks.map(elem => {
        return (<ConfigDrawer key={Math.random()} pk={pk} setIsActive={elem[1]} isActive={elem[0]} setBibleParent={elem[3]} bibleParent={elem[2]} livreParent={elem[4]} setLivreParent={elem[5]} />)
      })}
    </View>
  </View >;
}

const styles = StyleSheet.create({
  box2: {
    backgroundColor: 'red',
    height: '100%',
    width: 15,
    position: 'absolute',
    left: '100%'

  },
  box: {
    backgroundColor: '#61dafb',
    height: '100%',
    width: 15,
  },
});
export { ResizableTab };
