import React, { useState } from 'react';
import { View, Text } from 'react-native';
import bottomTab from '../style/bottomTab';
import { renderStyles as rs, ConvertCssToReactNativeStyle } from './renderStyles';
import { StyleSheet } from 'react-native';


let convertedStyleSheet = ConvertCssToReactNativeStyle(rs);
const [id,setid] = useState(0);
const styles = StyleSheet.create(convertedStyleSheet);

const getStyles = (type, subType) => {
    if (!styles[type]) {
        throw new Error(`Unknown style type '${type}'`);
    }
    if (!styles[type][subType]) {
        console.log(`No styles for ${type}/${subType}`)
        return styles[type].default;
    }
    return { ...styles[type].default, ...styles[type][subType] };
}



function InlineElement(props) {
    const [display, setDisplay] = useState(false);
    const toggleDisplay = () => setDisplay(!display);
    if (display) {
        return <div
            style={{
                ...props.style,
                paddingLeft: 8,
                paddingRight: 8,
                backgroundColor: "#CCC",
                marginTop: 16,
                marginBottom: 16
            }}
            onClick={toggleDisplay}
        >
            {props.children}
        </div>
    } else {
        return <span
            style={{
                verticalAlign: 'top',
                fontSize: 10,
                fontWeight: "bold",
                marginRight: 4,
                padding: 2,
                backgroundColor: "#CCC"
            }}
            onClick={toggleDisplay}
        >
            {props.linkText}
        </span>
    }
}
const renderers = {
    text: text => {setid(id+1)(<View key={`{text${id}`} style={{ paddingTop: 12 }}><Text>{text}</Text></View>)},
    chapter_label: number => <View><Text style={{
        ...getStyles('marks', "chapter_label"),
    }}>{number}</Text></View>,
    verses_label: (number, bcv, bcvCallback) =>
        bcv && bcv.length === 3 ?
            <Text
                onClick={() => bcvCallback(bcv)} style={{
                    ...getStyles('marks', "verses_label"),

    textDecoration: "underline",
                    color: "#00D"
                }}
            >
                {number}
            </Text> :
            <View style={{ flexDirection: 'row', paddingBottom: 10 }}>
                <Text style={{
                    ...getStyles('marks', "verses_label"),
                    marginRight: 10,


                }}>{number}</Text></View>
    ,
    paragraph: (subType, content, footnoteNo) =>
        ["usfm:f", "usfm:x"].includes(subType) ?
            <InlineElement
                style={getStyles('paras', subType)}
                linkText={subType === "usfm:f" ? `${footnoteNo}` : "*"}
            >
                {content}
            </InlineElement> :
            <View style={{ ...getStyles('paras', subType) }}>
                <Text style={{display:'flex',flexDirection:'row',flexWrap:'wrap'}}>
                    {content}
                </Text>
            </View>,

    wrapper: (subType, content) => <View key={content} style={getStyles('wrappers', subType)}>{content}</View>,
    wWrapper: (atts, content) => Object.keys(atts).length === 0 ?
        content :
        <Text
            style={{
                display: "flex",
                verticalAlign: "top",
                textAlign: "center",
                flexDirection:'column'
            }}
        >
            <Text key={content}>{content}</Text>
            {
                Object.entries(atts).map(
                    a =>
                        <Text key={atts}
                            style={{
                                fontSize: 9,
                                fontWeight: "bold"
                            }}
                        >
                            {`${a[0]} = ${a[1]}`}
                        </Text>
                )
            }
        </Text>,
    mergeParas: paras => paras,
}

export { renderers };
