import { View } from 'react-native';
import { library } from '@fortawesome/fontawesome-svg-core';
import { faGear } from '@fortawesome/free-solid-svg-icons/faGear';
import { BibleSelection } from './BibleSelection';
import Icon from 'react-native-vector-icons/FontAwesome';
import * as React from 'react';
import { Modal, Text, Button } from 'react-native-paper';
import AccordionItem from './AccordionItem';
library.add(faGear);

const MyComponent = ({pk}) => {
    const [visible, setVisible] = React.useState(false);

    const showModal = () => setVisible(true);
    const hideModal = () => setVisible(false);
    const containerStyle = {
        alignItems: 'center',
        width: '80%',
        heigth: '20%',
        alignSelf: 'center',
        backgroundColor: 'white',
        borderRadius: 20,
        borderWidth: 1,
        borderColor: 'grey'
    };



    return (
        <View style={{ flex: 1 }}>
            <View style={{ height: 50 }}></View>
            <Modal visible={visible} onDismiss={hideModal} contentContainerStyle={containerStyle}>
                <AccordionItem  title={"Bible"}><BibleSelection pk={pk}/></AccordionItem>
                <AccordionItem title={'Livre'}><Text>chapter</Text></AccordionItem>
                <AccordionItem title={"Chapitre"}><Text>chapter</Text></AccordionItem>
                <AccordionItem title={"Verse"}><Text>chapter</Text></AccordionItem>
            </Modal>
            <Button
                icon={() => <Icon name="gear" size={20} color="blue" />
                }

                onPress={showModal} />
        </View>
    );
};

export default MyComponent;
